import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeart } from '@fortawesome/free-solid-svg-icons'

export const ListItem = (props) => {
  console.log("ListItem rendering");

  useEffect(() => {
    console.log("ItemList mounted");

    return () => {
      console.log("ItemList unmounted");
    }
  }, [])

  const wishClass = () => props.wish ? 'active' : '';

  return (
    <li className="my-item item">
      <Link to={ `/items/${props.id}` }>
        <div className="fl">
          <div className="">
            <div className="img-wrap">
              <img src={props.imgSrc} />
            </div> 
          </div> 
          <div className="">
            <h3>{props.name}</h3>
            <p className="desc sm ellips">{props.desc}</p>
            <div className="fl">
              <div className="">{props.price}</div>
              <FontAwesomeIcon icon={faHeart} className={wishClass()} />
            </div>
          </div>    
        </div>
      </Link>
    </li>
  );
}

export default ListItem;
